                    }
                    break;
                case 4:
                    for (int i = 0; i < item.CarsComingIn.Length; i++)
                    {
                        if (item.CarsComingIn[i] != null)
                        {
                            switch (item.CarsComingIn[i].Direction)
                            {
                                case Direction.Left:
                                    if (item.CarsComingIn[i].X == 82)
